import com.eviware.soapui.support.GroovyUtils
//To get testcase name
def testCaseName = context.getTestCase().name
log.info ("Testcase name is " + testCaseName)
//to get testsuite name
def testSuiteName = context.getTestCase().getTestSuite().name
log.info ("TestSuite name is " + testSuiteName)
//To get the request xml
def reqXml = context.expand('${CreateNewProduct#request}')
log.info ("Request is " + reqXml)

//Setting the node value for SKU and name
//Attribute set Id, Price and Type id are being read by using property expansion
def sku = testRunner.testCase.getPropertyValue("SKU")
def name = testRunner.testCase.getPropertyValue("NAME")
def typeId = testRunner.testCase.getPropertyValue("TYPEID")
def setId = testRunner.testCase.getPropertyValue("SETID")
def price = testRunner.testCase.getPropertyValue("PRICE")

//Printing all the node values read from the custome properties
log.info ("SKU is " + sku)
log.info ("NAME is " + name)
log.info ("TYPE ID is " + typeId)
log.info ("SET ID is " + setId)
log.info ("PRICE is " + price)

def groovyUtils = new GroovyUtils(context)
xmlholdr = groovyUtils.getXmlHolder(reqXml)

//Setting all the node values for the request

xmlholdr.setNodeValue("//product/sku", sku)
xmlholdr.setNodeValue("//product/name", name)
xmlholdr.setNodeValue("//product/typeId", typeId)
xmlholdr.setNodeValue("//product/attributeSetId", setId)
xmlholdr.setNodeValue("//product/price", price)

//Fetched the updated xml
def updatedXml = xmlholdr.getXml();
log.info ("Updated Xml is " + updatedXml)

testRunner.testCase.getTestStepByName("CreateNewProduct").setPropertyValue("Request",updatedXml)


testRunner.testCase.testSuite.project.testSuites[testSuiteName].testCases[testCaseName].testSteps["CreateNewProduct"].run(testRunner,context)

